// src/features/skills/SkillsList.js
import { Col, Row } from "reactstrap";
import { selectAllSkills } from "./skillsSlice";
import Skill from "./Skill";
import { useSelector } from "react-redux";
import Loading from "../../components/Loading";
import Error from "../../components/Error";

const SkillsList = () => {
  const skills = useSelector(selectAllSkills);
  const isLoading = useSelector((state) => state.skills.isLoading);
  const errMsg = useSelector((state) => state.skills.errMsg);

  if (isLoading) {
    return <Loading />;
  }

  if (errMsg) {
    return <Error errMsg={errMsg} />;
  }

  // If you want a full‐width “no data” message, you can re‐enable this:
  // if (!skills || skills.length === 0) {
  //   return (
  //     <Col className="mt-4">
  //       <p>No skills available to display.</p>
  //     </Col>
  //   );
  // }

  return (
    <Col className="mt-4">
      <Row className="g-4">
        {skills.map((skill) => (
          <Col
            key={skill.id}
            xs="12"       /* 1 column on extra‐small screens */
            sm="6"        /* 2 columns on small screens (≥576px) */
            md="4"        /* 3 columns on medium screens (≥768px) */
            lg="3"        /* 4 columns on large screens (≥992px) */
            className="d-flex justify-content-center"
          >
            <Skill skill={skill} />
          </Col>
        ))}
      </Row>
    </Col>
  );
};

export default SkillsList;
