import { PROMOTIONS } from "../../app/shared/oldData/PROMOTIONS";
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { baseURL } from "../../app/shared/baseUrl";
import { mapImageUrl } from "../../utils/mapImageURL";
import { a } from "react-spring";

export const fetchPromotions = createAsyncThunk (
    'promotions/fetchPromotions',
    async () => {
        const response = await fetch(baseURL + 'promotions');
        if (!response.ok) {
            return Promise('Unable to fetch, status: ' + response.status);
        }
        const data = await response.json(); 
        return data; 
    }
);

const initialState = {
    promotionsArray: [],
    isLoading: true,
    errMsg: ''
};

const promotionsSlice = createSlice({
    name: 'promotions',
    initialState, 
    reducers: {},
    extraReducers: {
        [fetchPromotions.pending]: (state) => {
            state.isLoading = true;
        },
        [fetchPromotions.fulfilled]: (state, action) => {
            state.isLoading = false;
            state.errMsg = ''; 
            state.promotionsArray = mapImageUrl(action.payload);
        },
        [fetchPromotions.rejected]: (state, action) => {
            state.isLoading = false; 
            state.errMsg = action.errMsg ? action.error.message : 'Fetch Failed'
        }
    }
});

export const promotionsReducer = promotionsSlice.reducer; 

export const selectFeaturedPromotion = (state) => {
    return state.promotions.promotionsArray.find((promotion) => promotion.featured);

}
