import { Col } from "reactstrap";
import { selectAllSkills } from "./skillsSlice";
import Skill from "./Skill"
import { useSelector } from "react-redux";
import Loading from "../../components/Loading";
import Error from '../../components/Error';
const SkillsList = () => {
    const skills = useSelector(selectAllSkills);  
    const isLoading = useSelector((state) => state.skills.isLoading); 
    const errMsg = useSelector((state) => state.skills.errMsg);



    if (!skills || skills.length === 0) {
        return (
            <Col className='mt-4'>
                <p>No skills available to display.</p>
            </Col>
        );
    }

    return isLoading ? (
        <Loading />
    ) : errMsg ? (
        <Error errMsg={errMsg} /> 
    ) : (
        <Col className='mt-4'> 
            {
                skills.map((skill) => {
                    return (
                        <div className='d-flex mb-5' key={skill.id}>
                            <Skill skill={skill} />
                        </div>
                    )
                })
            }
        </Col>
    )
}

export default SkillsList;
