import {Card, CardImg, CardImgOverlay, CardTitle, CardHeader} from "reactstrap";
import { Link } from "react-router-dom";

const ProjectCard = ({project}) => {
    const {image, name, id} = project
    return(
        <Link to={`${id}`}>

            <Card>
            <CardHeader className="px-3 py-2 rounded"
                      style={{
                        /* solid backdrop so sub-pixel AA stays on */
                        backgroundColor: '#ffffff',

                        color: '#07df00',
                        fontFamily: '"Fira Code", monospace',
                        fontWeight: 500,
                        fontSize: '1.25rem',

                        /* hard shadow, integer offsets */
                        textShadow: '1px 1px 2px rgba(0,0,0,0.85)',

                        /* 4-px border you asked for earlier */
                        border: '4px solid rgba(0,0,0,0.9)',
                      }} >{name}
            </CardHeader>

                <CardImg 
                width='100' 
                src={image}
                alt={name}
            />


                {/* <CardImgOverlay>
                <CardTitle className="card-title-overlay px-3 py-2 rounded"
                    style={{
                        backgroundColor: 'rgba(65, 41, 25, 0.7)', // dark box behind text
                        color: 'rgba(244, 149, 55, 0.7)',
                        // fontWeight: 'bold',
                        textShadow: '1px 1px 2px rgba(0, 0, 0, 0.25)',
                        fontSize: '1.50rem',
                        }} >{name}</CardTitle>
                </CardImgOverlay> */}


            </Card>
        </Link>

    );
};


export default ProjectCard;
